void config_i2c_GLCD_Select(void){
	struct port_config pin_config;
	port_get_config_defaults( &pin_config);
	pin_config.direction  = PORT_PIN_DIR_OUTPUT;
	port_pin_set_config(PIN_PA23, &pin_config);
	port_pin_set_output_level(PIN_PA23, true);
}

void configure_i2c_master(void){
	struct i2c_master_config config_i2c_master;
	
	i2c_master_get_config_defaults(&config_i2c_master);
//	config_i2c_master.buffer_timeout = 1000;
	config_i2c_master.buffer_timeout = 10000;
	
	config_i2c_master.pinmux_pad0 = PINMUX_PA08D_SERCOM2_PAD0;
	config_i2c_master.pinmux_pad1 = PINMUX_PA09D_SERCOM2_PAD1;
	
	i2c_master_init(&i2c_master_instance, SERCOM2, &config_i2c_master);
	i2c_master_enable(&i2c_master_instance);
}


enum status_code i2cOut( struct i2c_master_module *const module, uint8_t toSlaveByte){

	SercomI2cm *const i2c_module = &(module->hw->I2CM);
	enum status_code tmp_status;

	if (!(i2c_module->STATUS.reg & SERCOM_I2CM_STATUS_BUSSTATE(2))) return STATUS_ERR_PACKET_COLLISION;

	_i2c_master_wait_for_sync(module);
	
	i2c_module->DATA.reg = toSlaveByte;
	
	tmp_status = _i2c_master_wait_for_bus(module);

	if (tmp_status != STATUS_OK) return tmp_status;
	if (i2c_module->STATUS.reg & SERCOM_I2CM_STATUS_RXNACK) return STATUS_ERR_OVERFLOW;
	
	return STATUS_OK;
}

